//Selector 0 is use to leave while

void MainScreenSelector(short selector)
{

	while (selector !=0)
	{
		switch (selector)
		{
			case 1:
			{
				selector = Title();
				break;
			}
			case 2:
			{
				selector = Menu ();
				break;
			}
			case 3:
			{
				selector = Classic ();
				break;
			}
		}
	}
}

