void ScoreMode::PrintScore (int xCur, int yCur)
{
	gb.display.setCursor( xCur, yCur );
	gb.display.print("Score : " );
	gb.display.println(this->score);	
}

bool ScoreMode::GameOverLose ()
{
	if (HeadInBody())
	{
		GameOverScreen (1);
		return true;
	}
	else if(riverPositionX[1] < (mapXMin) || riverPositionX[1] > (mapXMax) || riverPositionY[1] < (mapYMin) || riverPositionY[1] > (mapYMax))
	{
		GameOverScreen (1);
		return true;
	}
	else
		return false;
}
bool ScoreMode::GameOverWin ()
{
	if (riverPositionX[0] == 80)
	{
		GameOverScreen (0);
		return true;
	}
	else 
		return false;
}

//serpentissimo.GetRiverPosition need to be use before this function to have update value.
bool ScoreMode::HeadInBody()
{

	for (short i = 2 ; i < riverPositionX[0] + 1 ; i++ )
	{
		if (riverPositionX[1] == riverPositionX[i] && riverPositionY[1] == riverPositionY[i])
			return true;
	}
	
	return false;
}

//serpentissimo.GetRiverPosition need to be use before this function to have update value.
void ScoreMode::MoveFood (Food &food)
{
	const short nbrTile ((mapXMax+1)*(mapYMax+1));
	short fullSpace = 0;
	short mapSpace[10][8] {};
	
	for ( short i = 1 ; i < riverPositionX[0] + 1; i++ )
	{
		mapSpace [riverPositionX[i]][riverPositionY[i]] = 1;
		fullSpace ++;
	}
	
	short nextPosition = 32;
	if (fullSpace != nbrTile )
		nextPosition = random(0,(nbrTile - fullSpace));
	
	
	for ( short i = 0; i < nextPosition + 1 ; i++)
	{
		if ( mapSpace[i/8][i%8] == 1 )
			++nextPosition;
	}
	
	food.SetPosition( nextPosition / 8 ,nextPosition % 8 );
}

bool ScoreMode::EatGem()
{
	if (!this->gem.Safe(riverPositionX[1],riverPositionY[1]))
	{
		this->serpentissimo.Grow();
		this->MoveFood(gem);
        ++this->score;
		return true;
	}
	
	return false;
}

//Public methode

void ScoreMode::Play()
{
	byte mvtWanted = 5;

	bool gameOver = false;
	short time = 0;
	
	while(!gameOver)
	{

		while (!gb.update());
		gb.display.clear();


		gameOver = this->Update(time, mvtWanted);
		
		
		this->Draw();
		
		++time;
		
		if (time >= timePT * 50)
			time = 0;
	}
	this->Restart();
}

void ScoreMode::Restart()
{
	short xP(random(mapXMin, mapXMax + 1 ) );
	short yP(random(mapYMin, mapYMax + 1 ) );
	this->serpentissimo.SetPos(xP, yP);
	this->serpentissimo.Restart();
	//If we don't clear with new reset value we would keep the potential previous lose condition in memory
	this->serpentissimo.GetRiverPosition( riverPositionX, riverPositionY );
	this->score = 0;
	MoveFood(this->gem);
}

bool ScoreMode::Update(const short &time, byte &mvtWanted)
{

	if (gb.buttons.pressed(BUTTON_UP))
	{
		mvtWanted = 8;
	}
	else if (gb.buttons.pressed(BUTTON_RIGHT))
	{
		mvtWanted = 6;
	}
	else if (gb.buttons.pressed(BUTTON_DOWN))
	{
		mvtWanted = 2;
	}
	else if (gb.buttons.pressed(BUTTON_LEFT))
	{
		mvtWanted = 4;
	}
	
	if (GameOverLose()) 
	{
		return true;
	}
	if (GameOverWin()) 
	{
		return true;
	}
	
	if(gb.buttons.pressed(BUTTON_MENU))
	{
		GameOverScreen (1);
		return true;
	}
// /* Release: Uncomment for release  		
	if (gb.buttons.pressed(BUTTON_A))
	{
		mvtWanted = 5;
	}

	if (gb.buttons.pressed(BUTTON_B))
	{
		GameOverScreen (0);
		return true;
	}
// */
		
	
	if (time % timePT == 0 ) 
	{
		this->serpentissimo.Update(mvtWanted);
		this->serpentissimo.GetRiverPosition( riverPositionX, riverPositionY );
		if (this->EatGem())
			//Sound: Change this sound later
			gb.sound.playOK();
		else
			gb.sound.playTick();
	}
	return false;
}

void ScoreMode::Draw()
{
	this->serpentissimo.Draw();
	this->gem.Draw();
}

